import React from 'react';
import { Link } from 'react-router-dom';
import home from '../img/homeIcon.png';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { getItems } from '../actions/itemActions';
import PropTypes from 'prop-types';
import { itemActions } from '../actions/itemActions'
import initialState from '../reducers/itemReducer'


class Cities extends React.Component {
  constructor() {
    super();
    this.state= {
      cities: initialState,
      estado: true,
      search: ''
    };
  }

  updateSearch(event) {
    this.setState({search: event.target.value.substr(0,20)});
  }

  componentDidMount() {
   /* fetch('http://localhost:5000/cities/all')
    .then(function(response) {
      return response.json();
    }).then(function(json) {
  cities.push(json)
  console.log(json)
      });
    console.log(cities) */
    fetch('http://localhost:5000/cities/all')
    .then(results => {
    return results.json();
    }).then(data => {
      this.setState({cities: initialState});
  })}
  

  render () {
    const items = this.props.itemdos;
    return (
        <div>
          <input type="text"
            value={this.state.search}
            onChange={this.updateSearch.bind(this)}/>
          {items.map((city) => {
            return(
              <div key={city}>
              <ul>
                <li>{city.name}</li>
              </ul>
              </div>
            )})}
        <footer className="Main-footer">
            <Link to="/"><img src={home} className="Main-footer" alt="Home" /></Link>
        </footer>
      </div>
    )
  }
}

Cities.propTypes = {
  getItems: PropTypes.func.isRequired,
  item: PropTypes.object.isRequired
};

const mapStateToProps = (state) =>{ 
  console.log(state);
  
  return {
  itemdos: state.item[0].filter(
    (item) => {
      return item.name.indexOf(this.state.search) !== -1
    }
  )
}};

const mapDispatchToProps = dispatch => ({
  actions: bindActionCreators(getItems, dispatch)
 // editarLista:(listaNueva) => { dispatch({type: 'GET_ITEMS', items: listaNueva})}
})

export default connect(mapStateToProps, mapDispatchToProps)(Cities);

