          import React from 'react'
          import home from '../homeIcon.png'
          import { Route, Link, BrowserRouter as Router, Switch } from 'react-router-dom'
          import { connect } from 'react-redux';
          import { bindActionCreators } from 'redux';
          import { getItems } from '../actions/itemAction';
          import PropTypes from 'prop-types';
          import { itemActions } from '../actions/itemAction'
          import initialState from '../reducers/itemReducer'
          class CitiesPages extends React.Component {
            constructor() {
              super();
              this.state= {
                cities: initialState,
                estado: true,
              };
            }
            /*
            async componentDidMount(){
              //const recibir = await 
              fetch('http://localhost:5000/Cities/all')
              .then(results=>{
                return results.json();
              }).then(data=>{
                let ciudades = data.map((cit)=>{
                  return(
                    <div key={cit._id}>
                    <h4>{cit.name}, {cit.country}</h4>
                  </div>
                  )
              })
              this.setState({ciudades: ciudades});
            })
            }*/
            //https://blog.hellojs.org/fetching-api-data-with-react-js-460fe8bbf8f2
            render() {
              console.log(this.props.itemdos);
              
              const items = this.props.itemdos;
              return (
                <div className="mb-2">
                  <p>Entrada exitosa City</p>
                  <footer>
                    <div className="row">
                    
                      <div className="column mx-auto mt-4">
                      {
                        items.map((element)=> 
                        { 
                          console.log("ola");
                          
                          console.log(element);
                          return element.map((ciudad) =>
                          {
                            console.log(ciudad.country);
                          return <h4>{ciudad.country}</h4>
                          })
                        }
                      )
                      }
                        <Link to="/">
                          <img src={home} alt="f" className="home" />
                        </Link>
                      </div>
                    </div>
                  </footer>
                </div>
              )
            }
          }
          CitiesPages.propTypes = {
            getItems: PropTypes.func.isRequired,
            item: PropTypes.object.isRequired
          };
          const mapStateToProps = (state) =>{ 
              console.log(state.item);
              return {
            itemdos: state.item
          }};
          const mapDispatchToProps = dispatch => ({
            actions: bindActionCreators(getItems, dispatch)
            // editarLista:(listaNueva) => { dispatch({type: 'GET_ITEMS', items: listaNueva})}
          })
          export default connect(mapStateToProps, mapDispatchToProps)(CitiesPages);
