import React from 'react';
import { Route, Link, BrowserRouter as Router, Switch } from 'react-router-dom';
import home from '../img/homeIcon.png';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { getItems } from '../actions/itemActions';
import PropTypes from 'prop-types';
import { itemActions } from '../actions/itemActions'
import initialState from '../reducers/itemReducer'

class Cities extends React.Component {
  constructor() {
    super();
    this.state= {
      cities: initialState,
    };
  }

  render() {    
    const items = this.props.itemdos;
    return (
      <div className="mb-2">
          <div className="row">
            <div className="column mx-auto mt-4">
            {items.map((ciudad)=> { 
              return <h4 key={ciudad._id}><a href={`https://www.google.com/search?q=${ciudad.name}`}>{ciudad.name}</a> </h4>
            })}
        <footer>
              <Link to="/">
                <img src={home} alt="f" className="home" />
              </Link>
        </footer>
        </div>
      </div> 
    </div>
    )
  }
}

Cities.propTypes = {
  getItems: PropTypes.func.isRequired,
  item: PropTypes.object.isRequired
};
const mapStateToProps = (state) =>{ 
    console.log(state.item);
    return {
  itemdos: state.item
}};
const mapDispatchToProps = dispatch => ({
  actions: bindActionCreators(getItems, dispatch)
  // editarLista:(listaNueva) => { dispatch({type: 'GET_ITEMS', items: listaNueva})}
})
export default connect(mapStateToProps, mapDispatchToProps)(Cities);


